declare namespace java {
    namespace awt {
        namespace Component {
            namespace AccessibleAWTComponent {
                /**
                 * Fire PropertyChange listener, if one is registered,
                 * when shown/hidden..
                 * @since 1.3
                 */
                // @ts-ignore
                class AccessibleAWTComponentHandler extends java.lang.Object implements java.awt.event.ComponentListener {
                    // @ts-ignore
                    constructor()
                    // @ts-ignore
                    public componentHidden(e: java.awt.event.ComponentEvent): void
                    // @ts-ignore
                    public componentShown(e: java.awt.event.ComponentEvent): void
                    // @ts-ignore
                    public componentMoved(e: java.awt.event.ComponentEvent): void
                    // @ts-ignore
                    public componentResized(e: java.awt.event.ComponentEvent): void
                }
            }
        }
    }
}
