declare namespace java {
    namespace awt {
        namespace Component {
            namespace AccessibleAWTComponent {
                /**
                 * Fire PropertyChange listener, if one is registered,
                 * when focus events happen
                 * @since 1.3
                 */
                // @ts-ignore
                class AccessibleAWTFocusHandler extends java.lang.Object implements java.awt.event.FocusListener {
                    // @ts-ignore
                    constructor()
                    // @ts-ignore
                    public focusGained(event: java.awt.event.FocusEvent): void
                    // @ts-ignore
                    public focusLost(event: java.awt.event.FocusEvent): void
                }
            }
        }
    }
}
