declare namespace java {
    namespace awt {
        namespace Container {
            namespace AccessibleAWTContainer {
                /**
                 * Fire <code>PropertyChange</code> listener, if one is registered,
                 * when children are added or removed.
                 * @since 1.3
                 */
                // @ts-ignore
                class AccessibleContainerHandler extends java.lang.Object implements java.awt.event.ContainerListener {
                    // @ts-ignore
                    constructor()
                    // @ts-ignore
                    public componentAdded(e: java.awt.event.ContainerEvent): void
                    // @ts-ignore
                    public componentRemoved(e: java.awt.event.ContainerEvent): void
                }
            }
        }
    }
}
