declare namespace javax {
    namespace security {
        namespace auth {
            namespace login {
                /**
                 * A generic credential exception.
                 * @since 1.5
                 */
                // @ts-ignore
                class CredentialException extends javax.security.auth.login.LoginException {
                    /**
                     * Constructs a CredentialException with no detail message. A detail
                     * message is a String that describes this particular exception.
                     */
                    // @ts-ignore
                    constructor()
                    /**
                     * Constructs a CredentialException with the specified detail message.
                     * A detail message is a String that describes this particular
                     * exception.
                     * <p>
                     * @param msg the detail message.
                     */
                    // @ts-ignore
                    constructor(msg: string)
                }
            }
        }
    }
}
