declare namespace javax {
    namespace swing {
        namespace JComponent {
            namespace AccessibleJComponent {
                /**
                 * Fire PropertyChange listener, if one is registered,
                 * when focus events happen
                 * @since 1.3
                 */
                // @ts-ignore
                class AccessibleFocusHandler extends java.lang.Object implements java.awt.event.FocusListener {
                    // @ts-ignore
                    constructor()
                    // @ts-ignore
                    public focusGained(event: java.awt.event.FocusEvent): void
                    // @ts-ignore
                    public focusLost(event: java.awt.event.FocusEvent): void
                }
            }
        }
    }
}
