declare namespace javax {
    namespace swing {
        namespace plaf {
            namespace basic {
                namespace BasicComboPopup {
                    /**
                     * This listener changes the selected item as you move the mouse over the list.
                     * The selection change is not committed to the model, this is for user feedback only.
                     */
                    // @ts-ignore
                    class ListMouseMotionHandler extends java.awt.event.MouseMotionAdapter {
                        // @ts-ignore
                        constructor()
                        // @ts-ignore
                        mouseMoved(anEvent: java.awt.event.MouseEvent): void
                    }
                }
            }
        }
    }
}
