declare namespace javax {
    namespace swing {
        namespace plaf {
            namespace basic {
                namespace BasicTreeUI {
                    /**
                     * Repaints the lead selection row when focus is lost/gained.
                     */
                    // @ts-ignore
                    class FocusHandler extends java.lang.Object implements java.awt.event.FocusListener {
                        // @ts-ignore
                        constructor()
                        /**
                         * Invoked when focus is activated on the tree we're in, redraws the
                         * lead row.
                         */
                        // @ts-ignore
                        focusGained(e: java.awt.event.FocusEvent): void
                        /**
                         * Invoked when focus is activated on the tree we're in, redraws the
                         * lead row.
                         */
                        // @ts-ignore
                        focusLost(e: java.awt.event.FocusEvent): void
                    }
                }
            }
        }
    }
}
