declare namespace javax {
    namespace swing {
        namespace plaf {
            namespace basic {
                namespace BasicTreeUI {
                    /**
                     * TreeHomeAction is used to handle end/home actions.
                     * Scrolls either the first or last cell to be visible based on
                     * direction.
                     */
                    // @ts-ignore
                    class TreeHomeAction extends javax.swing.AbstractAction {
                        // @ts-ignore
                        constructor(direction: number /*int*/, name: string)
                        // @ts-ignore
                        direction: number /*int*/
                        // @ts-ignore
                        actionPerformed(e: java.awt.event.ActionEvent): void
                        // @ts-ignore
                        isEnabled(): boolean
                    }
                }
            }
        }
    }
}
