declare namespace javax {
    namespace swing {
        namespace text {
            namespace html {
                namespace FormView {
                    /**
                     * MouseEventListener class to handle form submissions when
                     * an input with type equal to image is clicked on.
                     * A MouseListener is necessary since along with the image
                     * data the coordinates associated with the mouse click
                     * need to be submitted.
                     */
                    // @ts-ignore
                    class MouseEventListener extends java.awt.event.MouseAdapter {
                        // @ts-ignore
                        constructor()
                        // @ts-ignore
                        public mouseReleased(evt: java.awt.event.MouseEvent): void
                    }
                }
            }
        }
    }
}
