declare namespace javax {
    namespace swing {
        namespace text {
            namespace html {
                namespace parser {
                    /**
                     * SGML constants used in a DTD. The names of the
                     * constants correspond the the equivalent SGML constructs
                     * as described in "The SGML Handbook" by  Charles F. Goldfarb.
                     * @see DTD
                     * @see Element
                     * @author Arthur van Hoff
                     */
                    // @ts-ignore
                    interface DTDConstants {
                        // @ts-ignore
                        readonly CDATA: number /*int*/
                        // @ts-ignore
                        readonly ENTITY: number /*int*/
                        // @ts-ignore
                        readonly ENTITIES: number /*int*/
                        // @ts-ignore
                        readonly ID: number /*int*/
                        // @ts-ignore
                        readonly IDREF: number /*int*/
                        // @ts-ignore
                        readonly IDREFS: number /*int*/
                        // @ts-ignore
                        readonly NAME: number /*int*/
                        // @ts-ignore
                        readonly NAMES: number /*int*/
                        // @ts-ignore
                        readonly NMTOKEN: number /*int*/
                        // @ts-ignore
                        readonly NMTOKENS: number /*int*/
                        // @ts-ignore
                        readonly NOTATION: number /*int*/
                        // @ts-ignore
                        readonly NUMBER: number /*int*/
                        // @ts-ignore
                        readonly NUMBERS: number /*int*/
                        // @ts-ignore
                        readonly NUTOKEN: number /*int*/
                        // @ts-ignore
                        readonly NUTOKENS: number /*int*/
                        // @ts-ignore
                        readonly RCDATA: number /*int*/
                        // @ts-ignore
                        readonly EMPTY: number /*int*/
                        // @ts-ignore
                        readonly MODEL: number /*int*/
                        // @ts-ignore
                        readonly ANY: number /*int*/
                        // @ts-ignore
                        readonly FIXED: number /*int*/
                        // @ts-ignore
                        readonly REQUIRED: number /*int*/
                        // @ts-ignore
                        readonly CURRENT: number /*int*/
                        // @ts-ignore
                        readonly CONREF: number /*int*/
                        // @ts-ignore
                        readonly IMPLIED: number /*int*/
                        // @ts-ignore
                        readonly PUBLIC: number /*int*/
                        // @ts-ignore
                        readonly SDATA: number /*int*/
                        // @ts-ignore
                        readonly PI: number /*int*/
                        // @ts-ignore
                        readonly STARTTAG: number /*int*/
                        // @ts-ignore
                        readonly ENDTAG: number /*int*/
                        // @ts-ignore
                        readonly MS: number /*int*/
                        // @ts-ignore
                        readonly MD: number /*int*/
                        // @ts-ignore
                        readonly SYSTEM: number /*int*/
                        // @ts-ignore
                        readonly GENERAL: number /*int*/
                        // @ts-ignore
                        readonly DEFAULT: number /*int*/
                        // @ts-ignore
                        readonly PARAMETER: number /*int*/
                    }
                }
            }
        }
    }
}
