declare namespace org {
    namespace spongepowered {
        namespace api {
            namespace text {
                namespace chat {
                    /**
                     * An enumeration of the vanilla chat visibility modes for a client.
                     */
                    // @ts-ignore
                    class ChatVisibilities extends java.lang.Object {
                        /**
                         * All chat is visible.
                         */
                        // @ts-ignore
                        readonly FULL: org.spongepowered.api.text.chat.ChatVisibility
                        /**
                         * No chat is visible.
                         */
                        // @ts-ignore
                        readonly HIDDEN: org.spongepowered.api.text.chat.ChatVisibility
                        /**
                         * Only {@link ChatTypes#SYSTEM} is visible.
                         */
                        // @ts-ignore
                        readonly SYSTEM: org.spongepowered.api.text.chat.ChatVisibility
                    }
                }
            }
        }
    }
}
