declare namespace org {
    namespace springframework {
        namespace core {
            namespace io {
                namespace support {
                    /**
                     * The default implementation for {@link PropertySourceFactory},
                     * wrapping every resource in a {@link ResourcePropertySource}.
                     * @author Juergen Hoeller
                     * @since 4.3
                     * @see PropertySourceFactory
                     * @see ResourcePropertySource
                     */
                    // @ts-ignore
                    class DefaultPropertySourceFactory extends java.lang.Object implements org.springframework.core.io.support.PropertySourceFactory {
                        // @ts-ignore
                        constructor()
                        // @ts-ignore
                        public createPropertySource(name: java.lang.String | string, resource: org.springframework.core.io.support.EncodedResource): org.springframework.core.env.PropertySource<any>
                    }
                }
            }
        }
    }
}
