declare namespace org {
    namespace springframework {
        namespace data {
            namespace mongodb {
                namespace core {
                    namespace aggregation {
                        namespace ArrayOperators {
                            namespace Reduce {
                                namespace PropertyExpression {
                                    /**
                                     * @author Christoph Strobl
                                     */
                                    // @ts-ignore
                                    interface AsBuilder {
                                        /**
                                         * Set the {@link AggregationExpression} resulting in the properties value.
                                         * @param expression must not be {#literal null}.
                                         * @return 
                                         */
                                        // @ts-ignore
                                        definedAs(expression: org.springframework.data.mongodb.core.aggregation.AggregationExpression): org.springframework.data.mongodb.core.aggregation.ArrayOperators.Reduce.PropertyExpression
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}
