declare namespace org {
    namespace springframework {
        namespace data {
            namespace mongodb {
                namespace core {
                    namespace mapping {
                        /**
                         * Simple constant holder for a {@link SimpleTypeHolder} enriched with Mongo specific simple types.
                         * @author Oliver Gierke
                         * @author Christoph Strobl
                         */
                        // @ts-ignore
                        abstract class MongoSimpleTypes extends java.lang.Object {
                            // @ts-ignore
                            public static readonly AUTOGENERATED_ID_TYPES: java.util.Set<java.lang.Class<any>> | Array<java.lang.Class<any>>
                            // @ts-ignore
                            public static readonly HOLDER: SimpleTypeHolder
                        }
                    }
                }
            }
        }
    }
}
