declare namespace org {
    namespace apache {
        namespace catalina {
            namespace util {
                /**
                 * A DOM writer optimised for use by WebDAV.
                 */
                // @ts-ignore
                class DOMWriter extends java.lang.Object {
                    // @ts-ignore
                    constructor(writer: java.io.Writer)
                    /**
                     * Prints the specified node, recursively.
                     * @param node The node to output
                     */
                    // @ts-ignore
                    public print(node: org.w3c.dom.Node): void
                }
            }
        }
    }
}
