declare namespace org {
    namespace apache {
        namespace catalina {
            namespace util {
                /**
                 * Utility class used to help generate return values for calls to
                 * {@link Object#toString()}.
                 */
                // @ts-ignore
                class ToStringUtil extends java.lang.Object {
                    // @ts-ignore
                    public static toString(contained: org.apache.catalina.Contained): string
                    // @ts-ignore
                    public static toString(obj: java.lang.Object | any, container: org.apache.catalina.Container): string
                    // @ts-ignore
                    public static toString(obj: java.lang.Object | any, manager: org.apache.catalina.Manager): string
                }
            }
        }
    }
}
